//If jQuery exists.
if(typeof jQuery!=="undefined"){jQuery(document).ready(function(){jQuery("*[data-icebox]").each(function(){jQuery(this).click(function(d){$e=jQuery(jQuery(this).attr("data-icebox-id"));var b=jQuery("<div>",{id:"ice-shadow"});var c=jQuery("<a>",{id:"icebox-close",href:"javascript:void(0);",title:"Close"}).text("X");var a=jQuery("<div>").append($e.html());var f=jQuery("<div>",{id:"icebox"}).append(c).append(a);jQuery("body").append(b).append(f);jQuery("#ice-shadow").fadeIn("400",function(){jQuery("#icebox").fadeIn("400",function(){jQuery(this).css("top",(((jQuery(window).height()-jQuery(this).outerHeight())/2)+jQuery(window).scrollTop()));jQuery(this).css("left",(((jQuery(window).width()-jQuery(this).outerWidth())/2)+jQuery(window).scrollLeft()));jQuery("#icebox-close, #ice-shadow").click(function(){jQuery("#icebox").fadeOut("400",function(){jQuery("#ice-shadow").fadeOut("400",function(){jQuery("#ice-shadow, #icebox").remove()})})})})});d.preventDefault();return false})})})}else{console.error("Unexpected error: jQuery must be loaded before running icebox.js.")};